(function () {

    function insertCSS(cssText) {


        var style = document.createElement("style");
        document.head.appendChild(style);

        /*IE8*/
        if (style.styleSheet)
            style.styleSheet.cssText += cssText;
        else
            style.appendChild(document.createTextNode(cssText));

    }
    
    function $(id) {
        return document.getElementById(id);
    }

    var api = {


        init: function () {

            insertCSS("body, html, #app {width: 100%; height: 100%;}body, html, form, h1,p { margin: 0; padding: 0; }a{    text-decoration: none;    color: #000;}#app {max-width:480px;margin:0 auto;}.navbar-title {padding-left: 2.08rem;padding-right: 2.08rem;height: 50px;line-height: 50px;font-size: 18px;text-align: center;} .ellipsis {white-space: nowrap; -o-text-overflow: ellipsis; text-overflow: ellipsis; overflow: hidden;}.file-icon {margin-right:5px;width:50px;height:50px;background: url(data:image/png;base64,);background-size: 50px; background-position: 50%;}.file-icon.folder { background: url(data:image/png;base64,);background-size: 50px; background-position: 50%;}.file-icon.mp4 {     background-image: url(data:image/png;base64,);background-size: 50px; background-position: 50%;}.file-icon.mp3 {         background-image: url(data:image/png;base64,);background-size: 50px; background-position: 50%;}.file-icon.txt {     background-image: url(data:image/png;base64,);background-size: 50px; background-position: 50%;}.file-icon.doc {     background-image: url(data:image/png;base64,);background-size: 50px; background-position: 50%;}p.name {    font-size: 15px;} p.time {    font-size: 12px; color: #ccc;}");
            
           
            var name = "";
            for(var i = 0; i < path.length; i++){
                var d = path[i];
                name = d.fileName;
            }
            
            var html = "<h1 class='navbar-title ellipsis'>"+name+"</h1>";
            for(var i = 0; i < data.length; i++){
                var d = data[i];
                
                if(d.downloadUrl && /^\/\//.test(d.downloadUrl)){
                   d.downloadUrl = "https:" + d.downloadUrl;
                }
                
//                html += "<div class='file-item'><a target='_blank' href='";
//                html += d.downloadUrl ? d.downloadUrl : d.isFolder ? d.fileId + ".html" : "javascript:;";
//                html += "'>";
//                html += "<table><tr><td><div class='file-icon ";
//                html += d.isFolder ? "folder" : d.fileType;
//                html += "'></div></td><td>";
//                html += "<p class='name ellipsis'>"
//                html += d.fileName;
//                html += "</p>";
//                html += "<p class='time'>"
//                html += d.createTime;
//                html += "</p></td></tr></table>";
//                html += "</a></div>";
                
                
               html += "<div class='file-item'><a target='_blank' href='";
                html += d.isFolder ? d.downloadUrl ? d.downloadUrl : d.fileId + ".html" : "javascript:;";
                html += "' ";
                if(d.downloadUrl){
                    html += "onclick='down(\"";
                     html+=d.downloadUrl
                }
                
                html += "\")'><table><tr><td><div class='file-icon ";
                html += d.isFolder ? "folder" : d.fileType;
                html += "'></div></td><td>";
                html += "<p class='name ellipsis'>"
                html += d.fileName;
                html += "</p>";
                html += "<p class='time'>"
                html += d.createTime;
                html += "</p></td></tr></table>";
                html += "</a></div>";
            }
           
            
            api.$dom = $("app");
            api.$dom.innerHTML = html;
            
            api.$loading = document.createElement("div");
            api.$loading.style.padding="80px";
            api.$loading.style.textAlign="center";
            api.$loading.innerHTML="正在处理下载...";
            api.$loading.style.display="none";
            document.body.appendChild(api.$loading);
            
        }
    };


    api.init();


    window.down = function(url){
        
        api.$dom.style.display="none";
        api.$loading.style.display="block";
        
        location = url;
        
        setTimeout(function(){
             api.$loading.style.display="none";
             api.$dom.style.display="block";
           
        },3000);
    }

})();
